import React, { useState } from "react";

import styled from "styled-components";
import Paper from "@material-ui/core/Paper";
import ButtonBase from "@material-ui/core/ButtonBase";




const StyledPaper = styled(Paper)`
  width: 100%;
  height: 100%;
  background-color: #ffffff;
  padding: 2em;
  &:hover {
    transform: translateY(-4px);
}
`;
const Title = styled.h2`
  color: #353336;
  font-size: 4em;
  text-align: center;
  font-weight: 300;
  margin: 0em;
  @media (max-width: 768px) {
    font-size: 3em;
  }
`;

const Base = styled(ButtonBase)`
  width: 100%;
  height: 100%;
`;

const Content = styled(Paper)`
background-color: #ffffff;
display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-evenly;
  align-items: center;

`
const Me = styled.img`
width: 35vh;
height: 30vh;
margin: 3em;
`
const MyBio = styled.p`
background-color: #ffffff;
color: #353336;
  font-size: 2em;
  text-align: center;
  font-weight: 300;
  margin: 0em;
`

export default function Bio() {

  const [open, setOpen] = useState(false)

  const showContent = () => {
    setOpen(prev => !prev)
  }
  return (
    <Base onClick={() => showContent()}>
      <StyledPaper>
        <Title>bio</Title>
        {open && <Content>
<Me alt="picture of me" src="https://res.cloudinary.com/dpfixnpii/image/upload/v1583284463/me_ppljqf.png"/>
<MyBio>words go here</MyBio>
          </Content>}

      </StyledPaper>
    </Base>
  );
}
