import styled from 'styled-components';

export const MainContainer = styled.div`
  display: grid;
  grid-template-rows: 1fr 2fr;
  width: 100%;
  height: 100vh;
  @media (max-width: 768px) {
    display: grid;
    grid-template-rows: 1fr 2fr;
  }
`;

export const StyledDiv1 = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  background-color: #ffffff;
  border: solid;
  border-color: #e0e3cf;
  border-width: 3px;
`;
