import React, {useState } from "react";
import Menu from "./Menu.js";
import { ShakeLittle } from 'reshake'
import {
  MainContainer,
  StyledDiv1,
  Name,
  StyledDiv2,
  Cactus,
  LinksDiv,
  MenuDiv,
  CactusTooltip,
  LinkImg
} from "./styles/App";



function App() {

const [state, setStae] = useState({
  gitHover: false,
  linkedinHover: false,
  resumeHover:false
})


const toggleHover = (name) => {
	setState({ ...state, [name]: !state.name})
}


  return (
    <MainContainer>
      <StyledDiv1>
        <Name>katherine szelag</Name>
        <CactusTooltip title="pretty fly for a cacti" placement="right" arrow>
          <Cactus
            alt="cactus"
            src="https://res.cloudinary.com/dpfixnpii/image/upload/v1583344933/cactus_2_i8rsfk.svg"
          />
        </CactusTooltip>
      </StyledDiv1>
      <StyledDiv2>
        <LinksDiv>
        <ShakeLittle  
        h={5}
        v={5}
        r={3}
        dur={300}
        int={10}
        max={100}
        fixed={true}
        fixedStop={false}
        freez={false}
        active={state.gitHover}>>
        <LinkImg onMouseEnter={toggleHover("gitHover")} onMouseLeave={toggleHover("gitHover")} alt="github" src="https://res.cloudinary.com/dpfixnpii/image/upload/v1583345284/github_kamxct.svg"/>
        </ShakeLittle>

        <LinkImg alt="resume" src="https://res.cloudinary.com/dpfixnpii/image/upload/v1583345272/document_xtuk0s.svg"/>
        <LinkImg alt="linkedin" src="https://res.cloudinary.com/dpfixnpii/image/upload/v1583345161/linkedin_kzdmwd.svg"/>
        </LinksDiv>
        <MenuDiv>
          <Menu />
        </MenuDiv>
      </StyledDiv2>
    </MainContainer>
  );
}

export default App;
