import React from "react";
import BackButton from "./Buttons/BackButton";
import Zoom from "@material-ui/core/Zoom";
import UpButton from "./Buttons/UpButton"
import { MainDiv, StyledPaper, Text} from "./styles/ContentPages"
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import styled from "styled-components";
import Grid from "@material-ui/core/Grid";


const StyledCard = styled(Card)`
  background-color: #899878;
  padding-top: 0.5rem;
  padding-bottom: 0.5rem;
  width: 100%;
  height: 36vh;
`;

const StyledCharliePic = styled.img`
height: 30vh;
  width: 30vh;

  @media (max-width: 768px) {
    height: 20vh;
    width: 20vh;
  }
`

export default function Bio(props) {
  return (
    <MainDiv>
      <BackButton onClick={() => props.transition("MENU")} />
      <Zoom in={true}>
        <StyledPaper>
          <Text>
            Yes, this page is in fact just a shameless tribute to Charlie the Golden Doodle ü§∑üèº‚Äç‚ôÄÔ∏è 
          </Text>
          <Grid container spacing={1}>
          <Grid item xs={12} sm={4}>
            <StyledCard>
              <CardContent>
                <StyledCharliePic alt="charlie" src="https://res.cloudinary.com/dpfixnpii/image/upload/v1583447551/746D92A2-831D-46A8-A194-99EAE40BF631_gn9snn.jpg" />
              </CardContent>

            </StyledCard>
          </Grid>

          </Grid>
          <UpButton onClick={() => props.scrollUp()}/>
        </StyledPaper>
      </Zoom>

    </MainDiv>
  );
}
